import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { apiGet, apiDelete } from '../utils/api';
import { hasPermission } from '../utils/auth';
import InteractionChart from './InteractionChart';
import CustomerTypeChart from './CustomerTypeChart';
import './CustomerList.css';

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCustomers = async () => {
      try {
        const data = await apiGet('/api/customers/getAllCustomers');
        setCustomers(data);
      } catch (error) {
        console.error('Error fetching customers:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCustomers();
  }, []);

  const handleViewDetails = (customerId) => {
    navigate(`/customers/${customerId}`);
  };

  const handleDelete = async (customerId) => {
    if (window.confirm('Are you sure you want to delete this customer and all their interactions?')) {
      try {
        await apiDelete(`/api/customers/deleteCustomer/${customerId}`);
        setCustomers(customers.filter(customer => customer.id !== customerId));
        setRefreshTrigger(prev => prev + 1);
      } catch (error) {
        console.error('Error deleting customer:', error);
        alert('Failed to delete customer');
      }
    }
  };

  const refreshCharts = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  if (loading) {
    return <div data-testid="loading">Loading customers...</div>;
  }

  if (customers.length === 0) {
    return <div data-testid="empty-msg">No customers found.</div>;
  }

  return (
    <div className="customer-list">
      <InteractionChart refreshTrigger={refreshTrigger} />
      <CustomerTypeChart refreshTrigger={refreshTrigger} />
      <h2>Customer List</h2>
      <table data-testid="customer-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Type</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.map(customer => (
            <tr key={customer.id}>
              <td>{customer.firstName} {customer.lastName}</td>
              <td>{customer.email}</td>
              <td>{customer.phoneNumber || 'N/A'}</td>
              <td>{customer.customerType}</td>
              <td>
                <button
                  onClick={() => handleViewDetails(customer.id)}
                  data-testid={`view-details-${customer.id}`}
                >
                  View Details
                </button>
                {hasPermission('DELETE_CUSTOMER') && (
                  <button
                    onClick={() => handleDelete(customer.id)}
                    data-testid={`delete-${customer.id}`}
                    className="delete-btn"
                  >
                    Delete
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CustomerList;
