import React, { useState } from 'react';
import InteractionChart from './InteractionChart';
import CustomerTypeChart from './CustomerTypeChart';
import { apiGet } from '../utils/api';
import './Analytics.css';

const Analytics = ({ refreshTrigger }) => {
  const [selectedInteractionType, setSelectedInteractionType] = useState('');
  const [selectedCustomerType, setSelectedCustomerType] = useState('');
  const [filteredCustomers, setFilteredCustomers] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchFilteredCustomers = async (customerType, interactionType) => {
    setLoading(true);
    try {
      let url = '/api/customers/getAllCustomersSimple';
      const params = new URLSearchParams();
      if (customerType) params.append('customerType', customerType);
      if (interactionType) params.append('interactionType', interactionType);
      if (params.toString()) url += '?' + params.toString();

      const customers = await apiGet(url);
      setFilteredCustomers(customers);
    } catch (error) {
      console.error('Error fetching filtered customers:', error);
      setFilteredCustomers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleInteractionTypeClick = (type) => {
    setSelectedInteractionType(type);
    setSelectedCustomerType('');
    fetchFilteredCustomers('', type);
  };

  const handleCustomerTypeClick = (type) => {
    setSelectedCustomerType(type);
    setSelectedInteractionType('');
    fetchFilteredCustomers(type, '');
  };

  const clearSelection = () => {
    setSelectedInteractionType('');
    setSelectedCustomerType('');
    setFilteredCustomers([]);
  };

  return (
    <div className="analytics-page">
      <h1>Analytics Dashboard</h1>

      <div className="charts-section">
        <InteractionChart refreshTrigger={refreshTrigger} />
        <div className="interaction-types-section">
          <h3>View Customers by Interaction Type</h3>
          <div className="type-buttons">
            <button onClick={() => handleInteractionTypeClick('PURCHASE')} className={selectedInteractionType === 'PURCHASE' ? 'active' : ''}>Purchase</button>
            <button onClick={() => handleInteractionTypeClick('INQUIRY')} className={selectedInteractionType === 'INQUIRY' ? 'active' : ''}>Inquiry</button>
            <button onClick={() => handleInteractionTypeClick('RETURN')} className={selectedInteractionType === 'RETURN' ? 'active' : ''}>Return</button>
          </div>
        </div>

        <CustomerTypeChart refreshTrigger={refreshTrigger} />
        <div className="customer-types-section">
          <h3>View Customers by Customer Type</h3>
          <div className="type-buttons">
            <button onClick={() => handleCustomerTypeClick('REGULAR')} className={selectedCustomerType === 'REGULAR' ? 'active' : ''}>Regular</button>
            <button onClick={() => handleCustomerTypeClick('PREMIUM')} className={selectedCustomerType === 'PREMIUM' ? 'active' : ''}>Premium</button>
            <button onClick={() => handleCustomerTypeClick('VIP')} className={selectedCustomerType === 'VIP' ? 'active' : ''}>VIP</button>
          </div>
        </div>
      </div>

      {(selectedInteractionType || selectedCustomerType) && (
        <div className="filtered-customers-section">
          <div className="section-header">
            <h2>Customers {selectedInteractionType ? `with ${selectedInteractionType} interactions` : `of type ${selectedCustomerType}`}</h2>
            <button onClick={clearSelection} className="clear-btn">Clear Selection</button>
          </div>

          {loading ? (
            <div>Loading customers...</div>
          ) : (
            <div className="customers-grid">
              {filteredCustomers.length > 0 ? (
                filteredCustomers.map(customer => (
                  <div key={customer.id} className="customer-card">
                    <h4>{customer.firstName} {customer.lastName}</h4>
                    <p>Email: {customer.email}</p>
                    <p>Phone: {customer.phoneNumber || 'N/A'}</p>
                    <p>Type: {customer.customerType}</p>
                  </div>
                ))
              ) : (
                <div>No customers found.</div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Analytics;
