import React, { useState, useEffect } from 'react';
import { apiGet } from '../utils/api';
import './InteractionChart.css';

const InteractionChart = () => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchInteractionStats = async () => {
      try {
        const customers = await apiGet('/api/customers/getAllCustomers');
        const stats = {};

        for (const customer of customers) {
          const interactions = await apiGet(`/api/customers/${customer.id}/interactions`);
          interactions.forEach(interaction => {
            stats[interaction.interactionType] = (stats[interaction.interactionType] || 0) + 1;
          });
        }

        const data = Object.entries(stats).map(([type, count]) => ({ type, count }));
        setChartData(data);
      } catch (error) {
        console.error('Error fetching interaction stats:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchInteractionStats();
  }, []);

  if (loading) return <div>Loading chart...</div>;
  if (chartData.length === 0) return <div>No interaction data available</div>;

  const maxCount = Math.max(...chartData.map(item => item.count));

  return (
    <div className="interaction-chart">
      <h2>Interaction Types Distribution</h2>
      <div className="chart-container">
        {chartData.map(({ type, count }) => (
          <div key={type} className="chart-bar">
            <div className="bar-label">{type}</div>
            <div className="bar-wrapper">
              <div 
                className="bar" 
                style={{ width: `${(count / maxCount) * 300}px` }}
              />
              <div className="bar-value">{count}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default InteractionChart;
